PRINT (N'Create or alter view [dbo].[VW_Phone_Group]')
GO
    create OR ALTER VIEW dbo.VW_Phone_Group
    as 
    select 
    CASE
        WHEN COUNT(c.Price) > 1 and MIN(Price) = 0 then CONVERT(NVARCHAR(MAX), FORMAT(MAX(c.Price),'#,##'))
        WHEN COUNT(c.Price) > 1 THEN CONCAT(CONVERT(nvarchar(Max),FORMAT(MIN(c.Price),'#,##')),' - ',CONVERT(nvarchar(Max),FORMAT(Max(c.Price),'#,##')))
        ELSE CONVERT(NVARCHAR(MAX), FORMAT(MAX(c.Price),'#,##'))
    END AS Price,
    a.Id as IdPhone,
    a.PhoneName as PhoneName,
    a.[Image] AS Image,
    b.Name as ProductionComanyName,
    a.CreateDate as CreateDate,
    MAX(c.Price) as PriceMax,
    MAX(d.Name) as RamName
    from Phones a left JOIN ProductionCompany b on a.IdProductionCompany = b.Id
    left JOIN PhoneDetailds c on a.Id = c.IdPhone
    LEFT JOIN Ram d on c.IdRam = d.Id
    GROUP BY
        a.Id, a.PhoneName, a.[Image], b.Name,a.CreateDate;
